import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";

// Core color types
export const RGBSchema = z.object({
  r: z.number().min(0).max(255),
  g: z.number().min(0).max(255),
  b: z.number().min(0).max(255),
});

export const LabSchema = z.object({
  L: z.number(),
  a: z.number(),
  b: z.number(),
});

export const OKLabSchema = z.object({
  L: z.number(),
  a: z.number(),
  b: z.number(),
});

// Pigment definition
export const PigmentSchema = z.object({
  id: z.string(),
  name: z.string(),
  code: z.string(),
  swatchHex: z.string(),
  colorant: OKLabSchema,
});

// Stroke data
export const StrokePointSchema = z.object({
  x: z.number(),
  y: z.number(),
  t: z.number(),
  pressure: z.number().optional(),
});

export const StrokeSchema = z.object({
  id: z.string(),
  pigmentId: z.string(),
  points: z.array(StrokePointSchema),
  brushRadius: z.number(),
  length: z.number(),
  amount: z.number(),
});

// Game state
export const GameStateSchema = z.object({
  target: RGBSchema,
  targetName: z.string(),
  strokes: z.array(StrokeSchema),
  amounts: z.record(z.string(), z.number()),
  totalAmount: z.number(),
  mixed: RGBSchema.nullable(),
  phase: z.enum(['painting', 'mixed', 'review']),
  score: z.number().nullable(),
  activePigmentId: z.string(),
  brushSize: z.number(),
});

// Session stats
export const SessionStatsSchema = z.object({
  attempts: z.number(),
  totalScore: z.number(),
  averageScore: z.number(),
  bestScore: z.number(),
  gamesPlayed: z.number(),
});

// Match history
export const MatchResultSchema = z.object({
  id: z.string(),
  targetColor: RGBSchema,
  targetName: z.string(),
  mixedColor: RGBSchema,
  score: z.number(),
  deltaE: z.number(),
  timestamp: z.number(),
  pigmentsUsed: z.record(z.string(), z.number()),
});

export const MatchHistorySchema = z.object({
  matches: z.array(MatchResultSchema),
});

// Type exports
export type RGB = z.infer<typeof RGBSchema>;
export type Lab = z.infer<typeof LabSchema>;
export type OKLab = z.infer<typeof OKLabSchema>;
export type Pigment = z.infer<typeof PigmentSchema>;
export type Stroke = z.infer<typeof StrokeSchema>;
export type StrokePoint = z.infer<typeof StrokePointSchema>;
export type GameState = z.infer<typeof GameStateSchema>;
export type SessionStats = z.infer<typeof SessionStatsSchema>;
export type MatchResult = z.infer<typeof MatchResultSchema>;
export type MatchHistory = z.infer<typeof MatchHistorySchema>;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import GamePage from "@/pages/game";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={GamePage} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

